package JS;

public class JsFor3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		   for(int i = 2; i <= 9; i+=2) {                 
		      System.out.println("天天天 " + i + "欽 天天天");
		      for(int j = 1; j<=9; j+=2) {
		      System.out.println(i + " * " + j + " = " + (i*j));
		      }
		   }
		
	}

}
